Klasse BST

class BST:
    def __init__(self, key=None, value=None):
        if key is None:
            self.root = None
        else:
            node = BSTNode(key, value)            
            self.root = node

    def insert(self, key, value=None, root=None):
        node = BSTNode(key, value)
        if self.root is None:
            self.root = node
            return

        if root is None:
            root = self.root

        if key &lt; root.key and root.left is None:
            root.left = node
            node.parent = root
            return

        if key &lt; root.key:
            root = root.left
            self.insert(key, value, root)


        if key &gt; root.key and root.right is None:
            root.right = node
            node.parent = root
            return

        if key &gt; root.key:
            root = root.right
            self.insert(key, value, root)

    def min(self, bst=None):
        if bst is None:
            minimum = self.root
        else:
            minimum =bst.root

        while minimum.left is not None:
            minimum = minimum.left

        return minimum

    def max(self, bst=None):
        if bst is None:
            maximum = self.root
        else:
            maximum = bst.root

        while maximum.right is not None:
            maximum = maximum.right

        return maximum

    def search(self, key, node=None):
        # If initial call or we've hit None in recursion
        if node is None:
            if self.root is None:  # Empty tree
                return -1
            node = self.root

        # Found the key
        if key == node.key:
            return node

        # Key doesn't exist in this path
        if key &lt; node.key:
            if node.left is None:
                return -1
            return self.search(key, node.left)
        else:  # key &gt; node.key
            if node.right is None:
                return -1
            return self.search(key, node.right)

    def delete(self, key):
        # Find the node to delete
        node = self.search(key)

        # If node not found, return
        if node == -1:
            return

        self._delete_node(node)

    def _delete_node(self, node):
        # Case 1: Node has no children (leaf node)
        if node.left is None and node.right is None:
            if node == self.root:
                self.root = None
            else:
                if node.parent.left == node:
                    node.parent.left = None
                else:
                    node.parent.right = None

        # Case 2: Node has only one child
        elif node.left is None:  # Has only right child
            if node == self.root:
                self.root = node.right
                node.right.parent = None
            else:
                if node.parent.left == node:
                    node.parent.left = node.right
                else:
                    node.parent.right = node.right
                node.right.parent = node.parent

        elif node.right is None:  # Has only left child
            if node == self.root:
                self.root = node.left
                node.left.parent = None
            else:
                if node.parent.left == node:
                    node.parent.left = node.left
                else:
                    node.parent.right = node.left
                node.left.parent = node.parent

        # Case 3: Node has two children
        else:
            # Find successor (smallest node in right subtree)
            successor = None
            current = node.right

            while current.left is not None:
                current = current.left

            successor = current

            # Copy successor's key and value to the node
            node.key = successor.key
            node.value = successor.value

            # Delete the successor (which has at most one right child)
            self._delete_node(successor)

    def iterate(self, node=None, result=None):
        # Initialize result list on first call
        if result is None:
            result = []

        # Use root if no starting node provided
        if node is None:
            if self.root is None:  # Empty tree
                return result
            node = self.root

        # In-order traversal: left -&gt; current -&gt; right
        if node.left is not None:
            self.iterate(node.left, result)

        result.append(node)

        if node.right is not None:
            self.iterate(node.right, result)

        return result

